import React from "react";
import "./NewExpense.css";
import ExpenseForm from "./ExpenseForm";
import { useState } from "react/cjs/react.production.min";

const NewExpense = (props) => {
  function onSaveExpenseDataHandler(enteredExpenseData) {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    console.log(expenseData);
    props.onAddExpense(expenseData);
  }

  const [showForm, setShowForm] = useState(false);

  function startFormHandler() {
    setShowForm(true);
  }

  return (
    <div className="new-expense">
      {!showForm && <button onClick={startFormHandler}>Add New Expense</button>}
      {showForm && <ExpenseForm onSaveExpenseData={onSaveExpenseDataHandler} />}
    </div>
  );
};

export default NewExpense;
